#!/usr/bin/zsh
pandoc doing_physics.md -o Book/doing_physics_with_quaternions.epub -t epub3 --toc --toc-depth=3 --epub-chapter-level=2 --epub-cover-image=images/Book/2021-07-11__doing_physics_with_quaternions__cover__DS.png  --css=Book/base.css -M title="Doing Physics with Quaternions" -M author="D. B. Sweetser" -M subtitle="A Forgotten Path to New Physics" -M rights="© 2021 D. B. Sweetser, CC BY-NC" -M publisher="Self published"
pandoc doing_physics.md -o Book/doing_physics_with_quaternions.pdf --pdf-engine=xelatex -t pdf --toc --toc-depth=3 --epub-chapter-level=2 --epub-cover-image=images/Book/2021-07-11__doing_physics_with_quaternions__cover__DS.png  --css=Book/base.css -M title="Doing Physics with Quaternions" -M author="D. B. Sweetser" -M subtitle="A Forgotten Path to New Physics" -M rights="© 2021 D. B. Sweetser, CC BY-NC" -M publisher="Self published"
pdftk Book/2021-07-11__doing_physics_with_quaternions__cover__DS.pdf Book/doing_physics_with_quaternions.pdf cat output Book/doing_physics.pdf
