#!/usr/bin/zsh
pandoc doing_physics.md -o Book/doing_physics_with_quaternions.epub -t epub3 --toc --toc-depth=3 --data-dir=Book --epub-chapter-level=2 -V linkcolor:blue --epub-cover-image=images/Book/doing_physics_with_quaternions__cover__DS.jpg  --css=Book/epub.css -M title="Doing Physics with Quaternions" -M author="D. B. Sweetser" -M subtitle="A Forgotten Path to New Physics" -M rights="© 2021 D. B. Sweetser, CC BY-NC" -M publisher="Self published"
pandoc doing_physics.md -o Book/doing_physics_with_quaternions.pdf --pdf-engine=xelatex -H Book/chapter_break.tex -f gfm -t pdf --toc --toc-depth=3 -V linkcolor:blue -V mainfont="DejaVu Serif" -V monofont="DejaVu Sans Mono" -V fontsize=12pt -V geometry:margin=2cm --epub-chapter-level=2 --epub-cover-image=images/Book/doing_physics_with_quaternions__cover__DS.jpg  --css=Book/epub.css -M title="Doing Physics with Quaternions" -M author="D. B. Sweetser" -M subtitle="A Forgotten Path to New Physics" -M rights="© 2021 D. B. Sweetser, CC BY-NC" -M publisher="Self published"
pdftk Book/2021-07-11__doing_physics_with_quaternions__cover__DS.pdf Book/doing_physics_with_quaternions.pdf cat output Book/doing_physics.pdf
